import React, { useState, useEffect } from 'react';
import axios from 'axios';


type Activity = {
  activity: string;
  type: string;
  participants: number;
  price: number;
  link: string;
  key: string;
};

const ActivityDisplayPage: React.FC = () => {
  const [activity, setActivity] = useState<Activity | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string>('');

  useEffect(() => {
    const fetchActivity = async () => {
      try {
        const response = await axios.get('https://www.boredapi.com/api/activity');
        setActivity(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Failed to fetch activity', error);
        setError('Failed to fetch activity. Please try again later.');
        setLoading(false);
      }
    };

    fetchActivity();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h1>Random Activity</h1>
      {activity && (
        <div>
          <p><strong>Activity:</strong> {activity.activity}</p>
          <p><strong>Type:</strong> {activity.type}</p>
          <p><strong>Participants:</strong> {activity.participants}</p>
          <p><strong>Price:</strong> {activity.price}</p>
          <p><strong>Link:</strong> <a href={activity.link} target="_blank" rel="noopener noreferrer">{activity.link}</a></p>
          <p><strong>Key:</strong> {activity.key}</p>
        </div>
      )}
    </div>
  );
};

export default ActivityDisplayPage;
