import React, { createContext, useContext, useState, useEffect } from 'react';
import { getCookie } from './authentication'; // Import your getCookie function

// Define the shape of your context state
interface AuthContextType {
  isAuthenticated: boolean;
  username: string | null;
}

// Create the context with a default value
const AuthContext = createContext<AuthContextType>({ isAuthenticated: false, username: null });

// Component to provide the context
export const AuthProvider: React.FC = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState<string | null>(null);

  useEffect(() => {
    const username = getCookie('username'); // Use your getCookie function to check if 'username' cookie exists
    if (username) {
      setIsAuthenticated(true);
      setUsername(username);
    }
  }, []);

  return (
    <AuthContext.Provider value={{ isAuthenticated, username }}>
      {children}
    </AuthContext.Provider>
  );
};

// Hook to use the authentication context
export const useAuth = () => {
    const context = React.useContext(AuthContext);
    if (context === undefined) {
      throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
  };
