import React, { createContext, useContext, useState, useEffect, PropsWithChildren } from 'react';
import { getCookie,deleteCookie } from './authentication'; // Make sure this path is correct

interface AuthContextType {
  isAuthenticated: boolean;
  username: string | null;
}

const AuthContext = createContext<AuthContextType>({ isAuthenticated: false, username: null });

// Use PropsWithChildren to explicitly type the children prop
export const AuthProvider: React.FC<PropsWithChildren<{}>> = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState<string | null>(null);

  useEffect(() => {
    const username = getCookie('username'); // Assuming 'username' is the name of the cookie
    if (username) {
      setIsAuthenticated(true);
      setUsername(username);
    }
  }, []);
  const logout = () => {
    deleteCookie('username'); // Use the name of your cookie
    setIsAuthenticated(false);
    setUsername(null);
};
  return (
    <AuthContext.Provider value={{ isAuthenticated, username }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
