import React, { Component } from 'react'; // Corrected import
import { Link } from 'react-router-dom';
import '../App.css';
import { getCookie } from '../utils/authentication';

interface IProps {}

interface IState {
  username: string;
  isAuthenticated: boolean;
}

export class Navbar extends Component<IProps, IState> {
  constructor(props: IProps) {
    super(props);
    // Set initial state
    this.state = {
      username: '',
      isAuthenticated: false,
    };
  }

  componentDidMount() {
    const username = getCookie('username'); // Retrieve username from cookie
    const isAuthenticated = username != null;
    this.setState({ username, isAuthenticated }); // Set state
  }

  render() {
    const { isAuthenticated, username } = this.state;

    return (
      <nav className="bg-beige-800 p-4 shadow-md">
        <div className="container mx-auto">
          <ul className="flex space-x-4">
            {isAuthenticated ? (
              <>
                <li>
                  <Link to="/profile" className="text-black hover:text-blue-300">Hi {username}</Link>
                </li>
                <li>
                  <Link to="/logout" className="text-black hover:text-blue-300">Logout</Link>
                </li>
              </>
            ) : (
              <>
                <li>
                  <Link to="/signup" className="text-black hover:text-blue-300">Sign Up</Link>
                </li>
                <li>
                  <Link to="/login" className="text-black hover:text-blue-300">Login</Link>
                </li>
              </>
            )}
          </ul>
        </div>
      </nav>
    );
  }
}

export default Navbar;
