import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import NavBar from './components/Navbar';
import imagePath from "./images/codepinklogo.png";






function App() {
  const [message, setMessage] = useState('');
  let items = ["Home","Product", "Service"];

  useEffect(() => {
    // Function to fetch data from the backend
    const fetchData = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/');
        const data = await response.text(); // Assuming the response is just plain text
        setMessage(data); // Update the state with the response
      } catch (error) {
        console.error('Error fetching data:', error);
        setMessage('Error fetching data'); // Update the state in case of an error
      }
    };

    fetchData(); // Call the function to fetch data
  }, []);
  return (
    <>

    <div>
      <NavBar
      brandName="My Brand" 
      imageSrcPath={imagePath} 
      navItems={items}/>
          </div>
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
        {message || 'Loading...'}
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
    </>
  );
}

export default App;
